import React, { useState } from 'react';
import { Redirect } from 'react-router-dom';

import './styles.css';

import HomePage from './HomePage';
import RecipePage from './RecipePage';

function App() {
    const [currentPage, setCurrentPage] = useState('home');
    const [recipeId, setRecipeId] = useState(null);

    const openPage = (page, id) => {
        setCurrentPage(page);
        setRecipeId(id);
    }

    return (
        <div>
        <header>
            <button onClick={() => openPage('home')}>Home</button>
            <button onClick={() => openPage('recipe', 1)}>Recipe</button>
        </header>
        <main>
            {currentPage === 'home' && <Redirect to="HomePage" />}
            {currentPage === 'recipe' && <Redirect to="RecipePage" />}
        </main>
        </div>
    );
}

export default App;
