import React, { useState, useEffect } from 'react';

import { usePageState } from './utils';

const HomePage = () => {
    const [data, setData] = useState(['']);

    const { currentPage, setCurrentPage, recipeId, setRecipeId, openPage } = usePageState();


    useEffect(() => {
        fetch('http://localhost:5000/api/recipes')
            .then(response => response.json())
            .then(data => setData(data))
            .catch(error => console.error('Error fetching data:', error));
    }, []);

    return (
        <div>
            <h1>Food Recipes</h1>
            <button onClick={() => openPage('home')}>Home</button>
            <button onClick={() => openPage('recipe', 1)}>Recipe</button>
              <table>
              <thead>
                  <tr>
                  <th>ID</th>
                  <th>Name</th>
                  </tr>
              </thead>
              <tbody>
                  {data.map(item => (
                  <tr key={item.id}>
                      <td>{item.id}</td>
                      <td>{item.name}</td>
                  </tr>
                  ))}
              </tbody>
              </table>
        </div>
    );
};

export default HomePage;
