import React, { Component } from 'react';

class PageRenderer extends Component {
    constructor(props) {
        super(props);

        this.state = {
            currentPage: 'home',
            recipeId: null,
            data: [],
            error: null
        };
    }

    componentDidMount() {
        this.fetchRecipesData();
    }

    fetchRecipesData = () => {
        fetch('http://localhost:5000/api/recipes')
        .then(response => response.json())
        .then(data => { this.setState({ data, error: null }) })
        .catch(error => console.error('Error fetching data:', error));
    }

    setCurrentPage = (pageName, id) => {
        this.setState({ currentPage: pageName, recipeId: id });
    }

    render() {
        const { currentPage, data, error } = this.state;
        let pageContent, tableContent;
        switch (currentPage) {
            case 'home':
                tableContent =
                <table>
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Name</th>
                            <th>Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        {data.map((item, index) => (
                            <tr key={index}>
                                <td>{item.id}</td>
                                <td>{item.name}</td>
                                <td><button onClick={() => (this.setCurrentPage('recipe', item.id), console.log('Item ' + item.id + ' clicked'))}>View</button></td>
                            </tr>
                        ))}
                    </tbody>
                </table>;
                
                pageContent = 
                <div>
                    <h1>Food Recipes</h1>
                    {tableContent}
                </div>;
                break;
            case 'recipe':
                let recipeItem = data.find(item => item.id === this.state.recipeId);
                pageContent =
                <div>
                    <h1>Recipe ID: {recipeItem.id}</h1>
                    <h2>Recipe name: {recipeItem.name}</h2>
                </div>;
                break;
        }

        return (
            <div>
                <main>
                    {pageContent}
                </main>
            </div>
        );
    }
};

export default PageRenderer;
