import React, { Component } from 'react';

class PageRenderer extends Component {
    constructor(props) {
        super(props);

        this.state = {
            currentPage: 'home',
            data: [],
            error: null
        };
    }

    componentDidMount() {
        this.fetchData();
    }

    fetchData = () => {
        fetch('http://localhost:5000/api/recipes')
        .then(response => response.json())
        .then(data => { this.setState({ data, error: null }) })
        .catch(error => console.error('Error fetching data:', error));
    }

    setCurrentPage = (pageName) => {
        this.setState({ currentPage: pageName });
    }

    render() {
        const { currentPage, data, error } = this.state;
        let pageContent, tableContent;
        switch (currentPage) {
            case 'home':
                tableContent = data.map((item, index) => (
                <tr key={index}>
                    <td>{item.id}</td>
                    <td>{item.name}</td>
                    <td>View</td>
                </tr>
                ));
                
                pageContent = 
                <div>
                <h1>Food Recipes</h1>
                <table>
                <thead>
                <tr>
                <th>ID</th>
                <th>Name</th>
                <th>Action</th>
                </tr>
                </thead>
                <tbody>
                </tbody>
                </table>
                </div>;
                break;
            case 'recipe':
                break;
        }

        return (
            <div>
                <main>
                    {pageContent}
                </main>
            </div>
        );
    }
};

export default PageRenderer;
