import React, { Component } from 'react';

class PageRenderer extends Component {
    constructor(props) {
        super(props);

        this.state = {
            currentPage: 'home',
            recipeId: null,
            recipesData: [],
            error: null
        };
    }

    componentDidMount() {
        this.fetchRecipesData();
    }

    fetchRecipesData = () => {
        fetch('http://localhost:5000/api/recipes')
        .then(response => response.json())
        .then(recipesData => { this.setState({ recipesData, error: null }) })
        .catch(error => console.error('Error fetching data:', error));
    }

    setCurrentPage = (pageName, id) => {
        this.setState({ currentPage: pageName, recipeId: id });
    }

    render() {
        const { currentPage, recipesData } = this.state;
        let pageContent, tableContent;
        switch (currentPage) {
            case 'home':
                tableContent =
                <table>
                <thead>
                    <tr>
                    <th>ID</th>
                    <th>Name</th>
                    <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {Array.from(new Set(recipesData.map(item => item.recipe_id))).map(recipeId => {
                    const recipe = recipesData.find(item => item.recipe_id === recipeId);
                    return (
                        <tr key={recipe.recipe_id}>
                        <td>{recipe.recipe_id}</td>
                        <td>{recipe.recipe_name}</td>
                        <td>
                            <button onClick={() => this.setCurrentPage('recipes', recipe.recipe_id)}>View</button>
                        </td>
                        </tr>
                    );
                    })}
                </tbody>
                </table>;

                
                pageContent = 
                <div>
                    <h1>Food Recipes</h1>
                    {tableContent}
                </div>;
                break;
            case 'recipes':
                let recipeItem = recipesData.find(item => item.recipe_id === this.state.recipeId);

                pageContent =
                <div>
            {recipesData.map(recipe => (
                <div key={recipe.recipe_id}>
                    <h2>{recipe.recipe_name}</h2>
                    <p><strong>Category:</strong> {recipe.recipe_category}</p>
                    <p><strong>Instructions:</strong> {recipe.recipe_instruction}</p>
                    <h3>Ingredients:</h3>
                    <ul>
                        {recipe.ingredients_json.map(ingredient => (
                            <li key={ingredient.ingredient_name}>
                                {ingredient.ingredients_quantity} {ingredient.ingredient_name}
                            </li>
                        ))}
                    </ul>
                </div>
            ))}
        </div>
                break;
            default:
                break;
        }

        return (
            <div>
                <main>
                    {pageContent}
                </main>
            </div>
        );
    }
};

export default PageRenderer;
