import React, { useState, useEffect } from 'react';

import './styles.css';

function App() {
    const [data, setData] = useState([]);

    useEffect(() => {
        fetch('http://localhost:5000/api/recipes')
            .then(response => response.json())
            .then(data => setData(data))
            .catch(error => console.error('Error fetching data:', error));
    }, []);

    return (
      <html>
          <head>
          </head>
          <body>
              <h1>Food Recipes</h1>
              <table>
              <thead>
                  <tr>
                  <th>ID</th>
                  <th>Name</th>
                  </tr>
              </thead>
              <tbody>
                  {data.map(item => (
                  <tr key={item.id}>
                      <td>{item.id}</td>
                      <td>{item.name}</td>
                  </tr>
                  ))}
              </tbody>
              </table>
          </body>
      </html>
    );
}

export default App;
