// utils.js
import { useState } from 'react';

export const usePageState = () => {
  const [currentPage, setCurrentPage] = useState('home');
  const [recipeId, setRecipeId] = useState(null);

  const openPage = (page, id) => {
    setCurrentPage(page);
    setRecipeId(id);
  };

  return {
    currentPage,
    setCurrentPage,
    recipeId,
    setRecipeId,
    openPage
  };
};
