import React, { Component } from 'react';

class PageRenderer extends Component {
    constructor(props) {
        super(props);

        this.state = {
            currentPage: 'home',
            recipeId: null,
            recipesData: [],
            error: null
        };
    }

    componentDidMount() {
        this.fetchRecipesData();
    }

    fetchRecipesData = () => {
        fetch('http://localhost:5000/api/recipes')
        .then(response => response.json())
        .then(recipesData => { this.setState({ recipesData, error: null }) })
        .catch(error => console.error('Error fetching data:', error));
    }

    setCurrentPage = (pageName, id) => {
        this.setState({ currentPage: pageName, recipeId: id });
    }

    render() {
        const { currentPage, recipesData, error } = this.state;
        let pageContent, tableContent;
        switch (currentPage) {
            case 'home':
                tableContent =
                <table>
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Name</th>
                            <th>Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        {recipesData.map((item, index) => (
                            <tr key={index}>
                                <td>{item.recipe_id}</td>
                                <td>{item.recipe_name}</td>
                                <td><button onClick={() => (this.setCurrentPage('recipes', item.recipe_id), console.log('Item ' + item.recipe_id + ' clicked'))}>View</button></td>
                            </tr>
                        ))}
                    </tbody>
                </table>;
                
                pageContent = 
                <div>
                    <h1>Food Recipes</h1>
                    {tableContent}
                </div>;
                break;
            case 'recipes':
                let recipeItem = recipesData.find(item => item.recipe_id === this.state.recipeId);

                pageContent =
                <div>
                    <h1>Food Recipes</h1>
                    <p>
                        ID: {recipeItem.recipe_id}<br />
                        NAME: {recipeItem.recipe_name}<br />
                        CATEGORY: {recipeItem.recipe_category}<br /><br />
                        INGREDIENTS: <br />{recipeItem.ingredients.split(',').map((ingredient, index) => (
                            <span key={index}>{ingredient}<br /></span>
                        ))}<br />
                    </p>
                </div>;
                break;
            default:
                break;
        }

        return (
            <div>
                <main>
                    {pageContent}
                </main>
            </div>
        );
    }
};

export default PageRenderer;
