import React, { Component } from 'react';

class PageRenderer extends Component {
    constructor(props) {
        super(props);

        this.state = {
            currentPage: 'home',
            recipeId: null,
            recipesData: [],
            error: null
        };
    }

    componentDidMount() {
        this.fetchRecipesData();
    }

    fetchRecipesData = () => {
        fetch('http://localhost:5000/api/recipes')
        .then(response => response.json())
        .then(recipesData => { this.setState({ recipesData, error: null }) })
        .catch(error => console.error('Error fetching data:', error));
    }

    setCurrentPage = (pageName, id) => {
        this.setState({ currentPage: pageName, recipeId: id });
    }

    render() {
        const { currentPage, recipesData } = this.state;
        let pageContent, tableContent;
        switch (currentPage) {
            case 'home':
                tableContent =
                <table>
                <thead>
                    <tr>
                    <th>ID</th>
                    <th>Name</th>
                    <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {Array.from(new Set(recipesData.map(item => item.recipe_id))).map(recipeId => {
                    const recipe = recipesData.find(item => item.recipe_id === recipeId);
                    return (
                        <tr key={recipe.recipe_id}>
                        <td>{recipe.recipe_id}</td>
                        <td>{recipe.recipe_name}</td>
                        <td>
                            <button onClick={() => this.setCurrentPage('recipes', recipe.recipe_id)}>View</button>
                        </td>
                        </tr>
                    );
                    })}
                </tbody>
                </table>;

                
                pageContent = 
                <div>
                    <h1>Food Recipes</h1>
                    {tableContent}
                </div>;
                break;
            case 'recipes':
// Assuming this code is within a React component
let recipeItem = recipesData.find(item => item.recipe_id === this.state.recipeId);


// Check if recipeItem exists and if it has ingredients
if (recipeItem && Array.isArray(recipeItem.ingredients)) {
    pageContent = (
        <div>
            <h1>Food Recipes</h1>
            <p>
                ID: {recipeItem.recipe_id}<br />
                NAME: {recipeItem.recipe_name}<br />
                CATEGORY: {recipeItem.recipe_category}<br /><br />
                INGREDIENTS:
                <ul>
                    {recipeItem.ingredients.map((ingredient, index) => (
                        <li key={index}>{ingredient.ingredient_name}: {ingredient.ingredient_quantity}</li>
                    ))}
                </ul>
                INSTRUCTIONS:<br /><br />{recipeItem.recipe_instruction}
            </p>
        </div>
    );
} else {
    // Handle the case when recipeItem or its ingredients are not properly defined
    pageContent = <div>No recipe found or ingredients not available</div>;
}


                break;
            default:
                break;
        }

        return (
            <div>
                <main>
                    {pageContent}
                </main>
            </div>
        );
    }
};

export default PageRenderer;
