import React, { useState, useEffect } from 'react';

const HomePage = (currentPage) => {
    const [data, setData] = useState(['']);

    useEffect(() => {
        fetch('http://localhost:5000/api/recipes')
            .then(response => response.json())
            .then(data => setData(data))
            .catch(error => console.error('Error fetching data:', error));
    }, []);

    const [setCurrentPage] = useState('home');
    const [recipeId, setRecipeId] = useState(null);

    const openPage = (page, id) => {
        setCurrentPage(page);
        setRecipeId(id);
    }

    return (
        <div>
            <h1>Food Recipes</h1>
            <header>
                <button onClick={() => openPage('home')}>Home</button>
                <button onClick={() => openPage('recipe', 1)}>Recipe</button>
            </header>
              <table>
              <thead>
                  <tr>
                  <th>ID</th>
                  <th>Name</th>
                  <th>Action</th>
                  </tr>
              </thead>
              <tbody>
                  {data.map(item => (
                  <tr key={item.id}>
                      <td>{item.id}</td>
                      <td>{item.name}</td>
                      <td>View</td>
                  </tr>
                  ))}
              </tbody>
              </table>
        </div>
    );
};

export default HomePage;
