import React, { useState, useEffect } from 'react';

import './styles.css';

import HomePage from './HomePage';
import RecipePage from './RecipePage';

function App() {
    const [data, setData] = useState(['']);

    const [currentPage, setCurrentPage] = useState('home');
    const [recipeId, setRecipeId] = useState(null);

    useEffect(() => {
        fetch('http://localhost:5000/api/recipes')
            .then(response => response.json())
            .then(data => setData(data))
            .catch(error => console.error('Error fetching data:', error));
    }, []);

    /*return (
      <html>
          <head>
          </head>
          <body>
              <h1>Food Recipes</h1>
              <table>
              <thead>
                  <tr>
                  <th>ID</th>
                  <th>Name</th>
                  </tr>
              </thead>
              <tbody>
                  {data.map(item => (
                  <tr key={item.id}>
                      <td>{item.id}</td>
                      <td>{item.name}</td>
                  </tr>
                  ))}
              </tbody>
              </table>
          </body>
      </html>
    );*/

    const openRecipePage = (page, id) => {
        setCurrentPage(page);
        setRecipeId(id);
    }

    return (
        <div>
        <header>
            <button onClick={() => setCurrentPage('home')}>Home</button>
            <button onClick={() => openRecipePage('recipe', 1)}>Recipe</button>
        </header>
        <body>
            <h1>Food Recipes</h1>
            <table>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Name</th>
                    </tr>
                </thead>
                <tbody>
                    {data.map(item => (
                    <tr key={item.id}>
                        <td>{item.id}</td>
                        <td>{item.name}</td>
                    </tr>
                    ))}
                </tbody>
            </table>
        </body>
        <main>
            {currentPage === 'home' && <HomePage />}
            {currentPage === 'recipe' && <RecipePage id={recipeId} />}
        </main>
        </div>
    );
}

export default App;
