      <html>
          <head>
          </head>
          <body>
              <h1>Food Recipes</h1>
              <table>
              <thead>
                  <tr>
                  <th>ID</th>
                  <th>Name</th>
                  </tr>
              </thead>
              <tbody>
                  {data.map(item => (
                  <tr key={item.id}>
                      <td>{item.id}</td>
                      <td>{item.name}</td>
                  </tr>
                  ))}
              </tbody>
              </table>
          </body>
      </html>
    );*/

    const openRecipePage = (page, id) => {
        history.push('/recipe?id=${id}');
    }

    return (
        <div>
        <header>
            <button onClick={() => setCurrentPage('home')}>Home</button>
            <button onClick={() => openRecipePage('recipe', 1)}>Recipe</button>
        </header>
        <main>
            {currentPage === 'home' && <HomePage />}
            {currentPage === 'recipe' && <RecipePage />}
        </main>
        </div>
    );
}

export default App;
